import React, { useState } from "react";
import "./NewExpenses.css";
import ExpenseForm from "./ExpenseForm";

const NewExpenses = (props) => {
    const saveExpenseDataHandler = (enteredExpensesObject) => {
        const expensesObject = {
            ...enteredExpensesObject,
            id: Math.random().toString()
        };
        props.onAddExpense(expensesObject);
    }

    const addButton = <button className='new-expense__actions' type='submit' onClick={theNewState}>
    Add New Expense</button>    


    const initialState = () => {
        return addButton    
    }

    const [expenseForm, setNewState] = useState(initialState)

    const theNewState = () => {
        return setNewState(
        <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
        )}

return (<div className='new-expense'>
    {expenseForm}
</div>
)
}

export default NewExpenses;
