import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Cards"
import "./Expenses.css" 
import ExpensesFilter from "./ExpensesFilter";



function Expenses(props) {
  const [filteredYear, setSelectedYear] = useState('2020')

  const filterSelectedYear = selectedYear => {
    setSelectedYear(selectedYear);
    console.log(selectedYear)
  }

  const filteredExpense = props.items.filter(expense => {
   return expense.date.getFullyYear().toString() === filterSelectedYear
  });

  return (
    <Card className="expenses">
      <ExpensesFilter selected={filteredYear} onChangeFilter={filterSelectedYear} />
      {filterInAction.map(expenses => <ExpenseItem
        key={expenses.id}
        title={expenses.title}
        amount={expenses.amount}
        date={expenses.date}/>)}
     </Card>
  )
}

export default Expenses
