import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Cards"
import "./Expenses.css" 
import ExpensesFilter from "./ExpensesFilter";



function Expenses(props) {
  const [filteredYear, setSelectedYear] = useState('')

  const filterSelectedYear = selectedYear => {
    setSelectedYear(selectedYear);  
  }

  const filteredExpense = props.items.filter(expenses => {
   return expenses.date.getFullYear().toString() === filteredYear
  });

  const expensesContent = <p>No expenses found</p>

  return (
    <Card className="expenses">
      <ExpensesFilter selected={filteredYear} onChangeFilter={filterSelectedYear} />
      {filteredYear.length === 0 && <p>No expenses found</p>}
      {filteredYear.length > 0 && filteredExpense.map(expenses => <ExpenseItem
        key={expenses.id}
        title={expenses.title}
        amount={expenses.amount}
        date={expenses.date}/>)}
     </Card>
  )
}

export default Expenses
