  const filterAction = () => {
    if (filterValue = props.year){
      return ExpenseItem
    }

  }


  return (
    <Card className="expenses">
      <ExpensesFilter />
      <ExpenseItem year={props.date.getFullYear()}
        title={expenses[0].title}
        amount={expenses[0].amount}
        date={expenses[0].date} />
      <ExpenseItem year={props.date.getFullYear()}
