            id: Math.random().toString()
        };
        props.onAddExpense(expensesObject);
    }

    const initialState = () => {
        <button className='new-expense__actions' type='submit' onClick={buttonCliked}>
            Add New Expense</button>
    }

    const [expenseForm, setNewState] = useState(initialState)

    const buttonClicked = setNewState => {
        return <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />
    }

    return (<div className='new-expense'>
